CREATE PROCEDURE [dbo].[SearchGoods]
@Id INT = null,
	@Brand nvarchar (30) = null,		
	@Model nvarchar (100) = null,		
	@CountryId int =  null,	
	@YearOfManufactureStart date = null,
	@YearOfManufactureEnd date = null,	
	@PowerConsumptionStart float = null,
	@PowerConsumptionEnd float = null,
	@RemoteStart bit = null,
	@NumberOfOperatingModes  int = null,
	@VolumeOfTheDustContainer float = null,
	@VolumeOfTheLiquidTank float = null,
	@AutomaticCleaning bit = null,
	@BodyMaterial nvarchar (30) = null,
	@TemperatureMaintenance bit = null,
	@ChamberVolume float = null,
	@InnerCoating nvarchar(30) = null,
	@BowlVolume float = null,
	@TypeOfHeating nvarchar(30) = null,
	@MaxLoading float = null,
	@TheVolumeOfTheDrum float= null,
	@DryerMode bit = null,
	@ColorId nvarchar(30) = null,
	@PriceStart decimal = null,
	@PriceEnd decimal = null
AS

declare @sendlerId int, @recipiendId int if exists( select * from dbo.Goods_Storage as GS where GS.GoodsId = @GoodsId and StorageId = @sender and (GS.QuantityGoods - @quantityGoods) > 0) begin Update dbo.Goods_Storage set QuantityGoods -= @quantityGoods, @sendlerId = Id where GoodsId = @GoodsId and StorageId = @sender end else THROW 60000, 'The product is not available in this store', 1;
if exists( select * from dbo.Goods_Storage as GS where GS.GoodsId = @GoodsId and StorageId = @recipiend ) begin Update dbo.Goods_Storage set QuantityGoods += @quantityGoods, @recipiendId = id where GoodsId = @GoodsId and StorageId = @recipiend end else begin insert into dbo.Goods_Storage values(@GoodsId,@recipiend,@quantityGoods) set @recipiendId = SCOPE_IDENTITY() end
SELECT GS.Id, GS.QuantityGoods,GS.GoodsId, GS.StorageId AS SenderId, @recipiend AS RecipiendId from dbo.Goods_Storage as GS where GS.StorageId = @sender and GS.GoodsId = @GoodsId;
SELECT GS.Id, GS.QuantityGoods,GS.GoodsId, GS.StorageId AS RecipiendId, @sender AS SenderId from dbo.Goods_Storage as GS where GS.StorageId = @recipiend and GS.GoodsId = @GoodsId;

